import React, { useState, useEffect } from 'react'
import Words from './Words.js'
import "./boardStyle.css"




export default function Board() {
    const [getLetters, setLetters] = useState([])
    const [getWord, setWord] = useState("");

    useEffect(() => {
        setWord(Words);
    }, []);

    function handleKeyPress() {
        window.onkeydown = function(e) {
            if(e.keyCode >= 65 && e.keyCode <= 122){
                let array = [...getLetters];
                array.push(e.key);
                setLetters(array)

                // setLetters((prevState) => [...prevState, e.key])
            }
            else if(e.keyCode === 8){
                //backspace pressed
                let array = [...getLetters];
                array.pop();
                setLetters(array)
            }
            else if(e.keyCode === 13){
                //enter pressed
                let oneWord = "";
                console.log(getLetters[5])

                for(let i = 0; i < getLetters[5]; i++){
                    oneWord = oneWord + getLetters[i];

                }
                console.log(oneWord);
                if(oneWord === getWord){
                    console.log("You Won");
                }
                else {
                    console.log("You Lost");
                }
            }
        }
    }
    handleKeyPress();
  return (
    <div>
        <div className='container'>
            <div className='boardContainer'>
                <div className='board'>{getLetters[0]}</div>
                <div className='board'>{getLetters[1]}</div>
                <div className='board'>{getLetters[2]}</div>
                <div className='board'>{getLetters[3]}</div>
                <div className='board'>{getLetters[4]}</div>
            </div>
            <div className='boardContainer'>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
            </div>
            <div className='boardContainer'>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
            </div>
            <div className='boardContainer'>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
            </div>
            <div className='boardContainer'>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
                <div className='board'></div>
            </div>
        </div>
        <h1>{getWord}</h1>
    </div>
  )
}
